INSERT INTO standard_reports (id,title,description,short_desc,category,filename,period,day_type) VALUES (175,'Storage Top Log Consumption','This report presents two independent views of information related to Log storage. The first highlights data stores that possess the largest amounts of Log storage.  The second lists Virtual Machines organized most to least in terms of their consumption of Log storage. ','Storage Top Log','Capacity Management/Storage','daily_digest_storage_top_log_grid','Daily',NULL);
