INSERT INTO user_reports (id,title,category,short_desc,description,xml_descriptor,period,day_type) VALUES (7,'Top 20 Hosts by CPU','Capacity Management','Top 20 Hosts by CPU','Top 20 Hosts by CPU during the most recent prior hour','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<user_report>\n  <title>&gt;Top 20 Hosts by CPU</title>\n  <table>user_pm_stats_by_hour</table>\n  <limit>20</limit>\n  <fields>\n    <field>instance_name</field>\n    <field>property_type</field>\n    <field>avg_value</field>\n    <field>recorded_on</field>\n    <field>hour_number</field>\n  </fields>\n  <filter_by>\n    <filter>\n      <field>property_type</field>\n      <op>equal</op>\n      <value>CPU</value>\n    </filter>\n  </filter_by>\n  <order_by>\n    <order>\n      <field>recorded_on</field>\n      <direction>desc</direction>\n      <order>1</order>\n    </order>\n    <order>\n      <field>hour_number</field>\n      <direction>desc</direction>\n      <order>2</order>\n    </order>\n    <order>\n      <field>avg_value</field>\n      <direction>desc</direction>\n      <order>3</order>\n    </order>\n  </order_by>\n</user_report>\n','Daily',NULL);
INSERT INTO user_reports (id,title,category,short_desc,description,xml_descriptor,period,day_type) VALUES (10,'Top 20 VMs by Storage','Capacity Management','Top 20 VMs by Storage','Top 20 VMs by Storage (M Bytes) from the most recent hourly values recorded','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<user_report>\n  <title>&gt;Top 20 VMs by Storage</title>\n  <table>user_vm_stats_by_hour</table>\n  <limit>20</limit>\n  <fields>\n    <field>instance_name</field>\n    <field>property_type</field>\n    <field>avg_value</field>\n    <field>recorded_on</field>\n    <field>hour_number</field>\n  </fields>\n  <filter_by>\n    <filter>\n      <field>property_type</field>\n      <op>equal</op>\n      <value>StorageAmount</value>\n    </filter>\n    <filter>\n      <field>property_subtype</field>\n      <op>equal</op>\n      <value>used</value>\n    </filter>\n  </filter_by>\n  <order_by>\n    <order>\n      <field>recorded_on</field>\n      <direction>desc</direction>\n      <order>1</order>\n    </order>\n    <order>\n      <field>hour_number</field>\n      <direction>desc</direction>\n      <order>2</order>\n    </order>\n    <order>\n      <field>avg_value</field>\n      <direction>desc</direction>\n      <order>3</order>\n    </order>\n  </order_by>\n</user_report>\n','Daily',NULL);
INSERT INTO user_reports (id,title,category,short_desc,description,xml_descriptor,period,day_type) VALUES (13,'Top 20 VMs by CPU','Capacity Management','Top 20 VMs by CPU','Top 20 VMs by CPU most recently recorded','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<user_report>\n  <title>&gt;Top 20 VMs by CPU</title>\n  <table>user_vm_stats_by_hour</table>\n  <limit>20</limit>\n  <fields>\n    <field>instance_name</field>\n    <field>property_type</field>\n    <field>avg_value</field>\n    <field>recorded_on</field>\n    <field>hour_number</field>\n  </fields>\n  <filter_by>\n    <filter>\n      <field>property_type</field>\n      <op>equal</op>\n      <value>VCPU</value>\n    </filter>\n  </filter_by>\n  <order_by>\n    <order>\n      <field>recorded_on</field>\n      <direction>desc</direction>\n      <order>1</order>\n    </order>\n    <order>\n      <field>hour_number</field>\n      <direction>desc</direction>\n      <order>2</order>\n    </order>\n    <order>\n      <field>avg_value</field>\n      <direction>desc</direction>\n      <order>3</order>\n    </order>\n  </order_by>\n</user_report>\n','Daily',NULL);
INSERT INTO user_reports (id,title,category,short_desc,description,xml_descriptor,period,day_type) VALUES (16,'Host Utilization for Prior Day','Capacity Management','Host Utilization Sumary for Prior Day','Host Utilization for Prior Day, listing each property having a non-zero level of utilization','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<user_report>\n  <title>&gt;Host Utilization for Prior Day</title>\n  <table>user_pm_stats_by_day</table>\n  <limit>500</limit>\n  <fields>\n    <field>instance_name</field>\n    <field>property_type</field>\n    <field>capacity</field>\n    <field>avg_value</field>\n    <field>recorded_on</field>\n  </fields>\n  <filter_by>\n    <filter>\n      <field>avg_value</field>\n      <op>greater than</op>\n      <value>0.00</value>\n    </filter>\n    <filter>\n      <field>recorded_on</field>\n      <op>equal</op>\n      <value>convert(date, dbo.date_sub(dbo.now(),1))</value>\n    </filter>\n    <filter>\n      <field>property_subtype</field>\n      <op>equal</op>\n      <value>utilization</value>\n    </filter>\n  </filter_by>\n  <order_by>\n    <order>\n      <field>property_type</field>\n      <direction>asc</direction>\n      <order>1</order>\n    </order>\n    <order>\n      <field>instance_name</field>\n      <direction>asc</direction>\n      <order>2</order>\n    </order>\n  </order_by>\n</user_report>\n','Daily',NULL);
